const Engine = (canvas, config) => {
	const cellSize = 10
	const w = Math.floor(canvas.width / cellSize)
	const h = Math.floor(canvas.height / cellSize)
	const _ctx = canvas.getContext('2d')
	const drawing = () => {
		const {x ,y} = cords;
		draw(x, y)
		world[x + y * w].setState(1)
	}
		const init = () => {
		canvas.addEventListener('mousemove',(event) =>{
			const {x, y} = getCords(event)
			cords = {x: Math.floor(x / cellSize), y: Math.floor(y / cellSize)}
		});
		canvas.addEventListener('mousedown',(event) =>{
			drawing()
			canvas.addEventListener('mousemove', drawing);
		});
		canvas.addEventListener('mouseup', () => {
			canvas.removeEventListener('mousemove',drawing);
		});
	}
	init()

	const Cell = (state) => (index) => {
		const j = Math.floor(index / w)
		const i = index % w
		const c = (i, j) => {
			const _i = i < 0 ? 0: i < w ? i : w - i
			const _j = j < 0 ? 0: j < h ? j : h - j
			return _i + _j * w
		}
		let _state = state;
		const setState = (s) => _state = s
		const getState = () => _state
		const siblings = () => [
			world[c(i - 1, j - 1)],
			world[c(i - 1, j)],
			world[c(i - 1, j + 1)],
			world[c(i, j - 1)],
			world[c(i, j + 1)],
			world[c(i + 1, j - 1)],
			world[c(i + 1, j)],
			world[c(i + 1, j + 1)]
		]
		return {
			getState,
			siblings,
			index,
			setState
		}
	}

	let world = [...Array(w*h).keys()].map(Cell(0))
	let cords = {}
	const getCords = event => {
		return {
			x:event.pageX - canvas.offsetLeft,
			y:event.pageY - canvas.offsetTop
		}
	}

	const alive = x => x >= 1

	const rules = (prev, res) => {
		if (alive(prev)){
			if (res > 2 && res < 3){
				return prev + 1
			}
			else {
				return prev
			}
		}
		else{
				if (res > 2){
					return prev + 1
				}
				else {
					return prev
				}
		}
	}
	const next = (cell) => {
		const prev = cell.getState()
		const res =  _dot(cell.siblings().map(s=>s.getState()), [...Array(8).fill(1)])
		return Cell(rules(prev, res))(cell.index)
	}

	const epoch = () => {
		world = world.map(next)
	}

	const _dot = (v1, v2) => v1.reduce((a, x, i) => a + x * v2[i], 0)

	const draw = (i, j) => {
		const state = world[i + j * w].getState()
		_ctx.beginPath();
		_ctx.fillStyle = `rgb(${ state * 180 },${ state * 50 },${ state * 30 })`
		_ctx.fillRect(i*cellSize,j*cellSize,cellSize, cellSize)
		//_ctx.arc(i*cellSize,j*cellSize,cellSize / 2, 0, Math.PI * 2)
		_ctx.fill()
	}


	const render = () => setInterval(() => {
		epoch()
		_ctx.clearRect(0, 0, canvas.width, canvas.height)
		for (let i = 0; i < w; ++i){
			for (let j = 0; j < h; ++j){
			//	if (alive(world[i + j*w])) {
					draw(i,j)
		//		}
			}
		}
	}, 100)

	return {
		render
	}
}




export default Engine
