import React, {Fragment, Component} from 'react'
import './Chat.css'
import { Container, Input, Button } from 'reactstrap'


const Message = ({message}) => {
	console.log(message)
	return(
	<Fragment>	
	<Container className='message'>
		<img width='30px' height='30px' src={message.url}/>
		<h2> {`${message.name}:`} </h2>
		<h3> {`_${message.text}`} </h3>
	</Container>
	</Fragment>
	)
}

export class Chat extends Component {
	state = {
		value: '' 
	}
	handle = event => {
		if (event.keyCode == 13){
			this.setState({
				value:event.target.value
			})
			event.target.value = ''
		}
	}

	render(){
		return (
				<Container className='chat w-100'>
					<Container className='textarea'>
						{this.props.messages.map((message, id) =>  <Message key={id} message={{...message, text:this.state.value}}/>)}
					</Container>
					<Container className='input'>
						<Input onKeyDown={this.handle}/>
						<Button color='success'>+</Button>
					</Container>
				</Container>
			)
		}
}

Chat.defaultProps = {
	messages: []
}
