import React, {Fragment} from 'react'
import { 
  Modal
} from '../../components'

const text = "В современном мире каждый выживает как может, но никто даже не догадывается какая война происходит у наших ног, рук, в наших кружках, у дверных ручек общественных местаъ. Это кровавая война (у них вообще есть кровь ?? лол=))) Может идти бесконечно и только ты можешь это исправить"

export const AboutModal = () => {
  return (
		 <Modal appear={true} to='/game' close={(x)=>x} closeKey={13}>
          <hr/>
            <p className='text-justify'>{text} </p>
          <hr/>
            <p className='text-center'>
                Подсказка: <p className='discription-text'> разбирайся сам </p>
            </p>
      </Modal>
	)
}
