import React, { Fragment } from 'react'
import './GameBar.css'
import { Container, Button } from 'reactstrap'
import {
	Message, Input
} from '../../components'



export const GameBar = ({value, color, url, text}) => {
	return (
		<Container fluid className='game-bar_container d-flex justify-content-left align-items-center'>
			<img width='100px' height='100px' src={url}/>
			<Message placement='top' text={text}/>
			<div className='game-bar' style={{width:value, background:color}}/>  
			<Input/>
		</Container>
	)
}
