import React, {Fragment} from 'react'
import './Statistics.css'
import { Container } from 'reactstrap'
import { GameBar } from '../'
import { Message } from '../../components'

const StatisticsItem = ({score, url}) => {
	return (
			<Container className='statistic-item'>
				<img width='50px' height='50px' src={url}/>
				<p> {score} </p>
				<Message placement='bottom' />
			</Container>
	)
}

export const Statistics = ({list, urls}) => {
	return (
			<ul className='statistics statistics-list'>
				{list.map((el, id) => <li key={id}> <StatisticsItem score={el} url={urls[id]}/> </li>)}			
			</ul>
		)
}
Statistics.defaultProps = {
	list: []
}
