import React, { Fragment, Component } from 'react'
import {Container, Input, Label } from 'reactstrap'
import './ClassSelector.css'

const HeroCard = ({url}) => {
	return(
			<button className='heroes'>
				<img width='100px' height='100px' src={url}/>
			</button>
	)
}

const ClassSelector = ({configs, description}) => {
		const { urls, descriptions } = configs
		return (
			<Container className='class-selector'>
				<Container className='class-selector__classes'>
					{urls.map((url, id) => <HeroCard onClick={this.choice(id)} key={id} url={url}/>)}
				</Container>
				{ description &&
					<Container className='class-selector__description border'>
						<p>this.</p>
					</Container>
				}
			</Container>
		)
}
