import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Button } from 'reactstrap'
import {
  plus,
  minus
} from './actions'
import './Game.css'

class Game extends Component {
constructor(props){
   super(props)
   this._canvas = React.createRef()
}
componentDidMount(){
  this.resize()
}

resize = () => {
  console.log(this._canvas.current.width,this._canvas.current.height)
  const parent = this._canvas.current.parentElement;
  this._canvas.current.width = parent.clientWidth
  this._canvas.current.height = parent.clientHeight
}

render(){
  return (
    <Fragment>
      <div> {this.props.count} </div>
      <div>
        <Button onClick={this.props.plus} >+</Button>
        <Button onClick={this.props.minus}>-</Button>
      </div>
      <div  className='h-100 w-100 background'>
        <canvas  ref={this._canvas}>
        </canvas>
      </div>
    </Fragment>
  )
}
}

const mapStateToProps = state => {
  return {
    count: state
  }
}
const mapDispatchToProps = dispatch => {
  return {
    plus: () => dispatch(plus()),
    minus: () => dispatch(minus())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Game);
