import React, { createRef, Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Button, Container } from 'reactstrap'
import {
  plus,
  minus
} from './actions'
import './Game.css'
import {
  Chat,
  Statistics,
  GameBar
} from './modules'
import { 
  BlurContainer,
} from './components'

import {
  InitModal
} from './containers'
import { selectRace } from './actions'
import Engine from './engine'

//rules for bonus 
  const alive1 = x => x >= 1

  const rules1 = (prev, res, alive) => {
    if (alive(prev)){
      if (res ==  2 || res == 3 ){
        return prev + 2
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive3 = x => x == 1

  const rules3 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 2 || res == 3 ){
        return 1
      }
      else {
        return 0
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive2 = x => x >= 1

  const rules2 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 1 || res == 4 ){
        return prev + 1
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        if (res == 4){
        return 2
      }
      else {
        return 0
      }
    }
  }



const iconUrls = ['./icon1.png', './icon2.png', './icon3.png']
const shadow = [{r:100,g:100,b:255},{r:255,g:100,b:100},{r:5,g:100,b:5}]
const urls = ['./hero1.png', './hero.png', './hero2.png', './waterbear.png']
const images = urls.map( url => new Image())
images.forEach((img, id) => img.src = urls[id])

const rules = [{alive:alive3, rules:rules3,image: images[0],shadow:shadow[0]}, {alive:alive3, rules:rules3, image:images[1],shadow:shadow[1]}, {alive:alive3, rules:rules3, image:images[2],shadow:shadow[2]}]

const configs = {
  urls: iconUrls,
  descriptions: ['супер быстрые','супер сильные',"супер плодовитые"] 
}

class Game extends Component {
state = {
  statistics: [],
  isLogin: false,
  text:''
}
constructor(props){
   super(props)
   this._canvas = createRef()
   this.engine = null
}
componentDidMount(){
  this.init()
  window.addEventListener('resize', this.init)
}
init = () => {
  this.resize()
  this.engine = Engine(this._canvas.current)
  rules.forEach(this.engine.addConfig)
  this.engine.setLoot({
    img:images[3]
  })
}

resize = () => {
  const parent = this._canvas.current.parentElement;
  this._canvas.current.width = parent.clientWidth
  this._canvas.current.height = parent.clientHeight
}

login = () => {
  this.change(this.props.num)()
  this.setState({
    isLogin: true
  })
}

logout = () => {
  this.setState({
    isLogin: false
  })
}

toggle = () => {
  let isOn = false;
  let id = null
  return () => {
    isOn = !isOn
    if (isOn) {
      id = setInterval(()=>{
        this.engine.render()
        this.setState({
          statistics:this.engine.getStatistics()
        })
      }, 100)
    }
    else{
      clearInterval(id)
    }
  }
}

change = (num) => () => {
  this.engine.use(num)
}
rand = () => this.setState({text:Math.random()})

render(){
  const { statistics } = this.state
  const  color = shadow[this.props.num - 1]
  return (
    <Fragment>
      <InitModal appear={!this.state.isLogin} login={this.login} configs={configs} />
       <BlurContainer  kernel={14} is={!this.state.isLogin}>
        <div  className='h-100 w-100 background tools'>
          <canvas  ref={this._canvas}/>
          <Statistics list={statistics} urls = {iconUrls}/>
          <Button  color='danger' onClick={this.toggle()}> on </Button>
          <Container className='d-flex'>
            <Button onClick={this.rand}> kek </Button>
            <GameBar value={statistics[this.props.num - 1]} text={this.state.text} url={iconUrls[this.props.num - 1]}  color={`rgb(${color.r},${color.g},${color.b}`}/>
          </Container>
        </div>
      </BlurContainer>
    </Fragment>
  )
}
}

/*
          <Chat messages={[{url:iconUrls[1],name:'nik', text:'kk'},{url:iconUrls[1],name:'nik', text:'kk'},{url:iconUrls[1],name:'nik', text:'kk'},{url:urls[2], name:'nik',text:'kekw'},{url:urls[0],name:'nik', text:'kek'}]}/>
*/

const mapStateToProps = state => {
  return {
    num: state.race + 1
  }
}

export default connect(mapStateToProps)(Game)
