const rootReducer = (state = 0, action) => {
	switch(action.type){
		case 'SELECT_NAME':
			return {
				...state,
				name: action.payload
			}
		case 'SELECT_RACE':
			return {
				...state,
				race: action.payload
			}
		case 'SET_POPULATION_SIZE':
			return {
				...state,
				population: action.payload
			}
	}
}


export {
	rootReducer
}
