import {
	SELECT_RACE,
	SELECT_NAME,
	SET_POPULATION_SIZE
} from '../constants'

const initState = {
	race: 0,
	name: ''
}

const rootReducer = (state = initState, action) => {
	switch(action.type){
		case SELECT_NAME:
			return {
				...state,
				name: action.payload
			}
		case SELECT_RACE:
			return {
				...state,
				race: action.payload
			}
		case SET_POPULATION_SIZE:
			return {
				...state,
				population: action.payload
			}
		default:
			return state
	}
}


export {
	rootReducer
}
