import React, {Fragment, Component, PureComponent} from 'react'
import './Message.css'
import {
	Container
} from 'reactstrap'


const stl = (position) =>  {
	const pos = (position === 'bottom') ? '0px,70px' : '50px,-50px'
	return {
		transform: `translate(${pos})`
	}
}

const Message = ({text, placement}) => {
	return(
		<div className='my_message'  style={stl(placement)}>
			{text}
		</div>
	)
}


class MessageContainer extends Component{
	state = {
		is:false,
		text:undefined
	}
	//magik blood sugar =))
	static getDerivedStateFromProps(props, state){
			if (!state.is){
				if (props.text !== state.text){
					return {
						is: true,
						text: props.text
					}
				}
				else {
					return {
						is: state.is,
						text:props.text
					}
				}
			}
			else{
				if (props.text !== state.text){
					return {
						is: state.is,
						text: props.text
					}
				}
				else {
					return {
						is: state.is,
						text:state.text
					}
				}
			}
	}	

	componentDidUpdate(prev, state){
		if (state.is){
			clearTimeout(this.id) 
			this.id = setTimeout(this.close, 2000)
		}
	}

	close = () => this.setState({is: false})

	render(){
		const { text } = this.props
		return(
			<Fragment>
				{this.state.is && <Message text={text} placement={this.props.placement}/>}
			</Fragment>
		)
	}
}

export {
	MessageContainer as Message
}
