import React, { Fragment, Component } from 'react'
import {Container, Input, Label } from 'reactstrap'
import './ClassSelector.css'
import { connect } from 'react-redux'

const HeroCard = ({url, onClick}) => {
	return(
			<button onClick={onClick} className='heroes'>
				<img width='100px' height='100px' src={url}/>
			</button>
	)
}

const  ClassSelector =  (props) => {
	const choice = (id) => () => this.props.initRace(id)
		const { configs, description } = this.props
		const { urls, descriptions } = configs
		return (
			<Container className='class-selector'>
				<Container className='class-selector__classes'>
					{ urls.map((url, id) => <HeroCard onClick={choice(id)} key={id} url={url}/>)}
				</Container>
				{ description &&
					<Container className='class-selector__description border'>
						<p>{descriptions[props.id]}</p>
					</Container>
				}
			</Container>
		)
}

const mapStateToProps = () => {

}
const mapDispatchToProps = () => {}

const ConnectedComponent = connect(mapStateToProps, mapDispatchToProps)(ClassSelector)

export {
	ConnectedComponent as ClassSelector
}
