import React, {Fragment, Component} from 'react'
import './Message.css'
import {
	PopoverBody,
	Popover
} from 'reactstrap'



const Message = ({text, placement, isOpen}) => {
	return(
		<Popover placement={placement} isOpen={isOpen}>
			<PopoverBody>
				{text}
			</PopoverBody>
		</Popover>
	)
}


class MessageContainer extends Component{
	state = {
		is: true
	}

	static getDerivedStateFromProps(nextProps, prevState){
		if (nextProps.isOpen){
			setTimeout(this.close, 2000)
		}
		return {
			is: nextProps.isOpen
		}
	}

	close = () => this.setState({is: false})
	render(){
		const {text, isOpen} = this.props
		return(
				<Message text='kek' placement='bottom' isOpen={this.state.is}/>
		)
	}
}

export {
	MessageContainer as Message
}
