const initConfig = {
	alive: x => x >= 1,
	rules: x => 0,
	name: 'init'
}

const argmax = array => {
	return array.length ? array.map((x, i) => [x, i]).reduce((r, a) => (a[0] > r[0] ? a : r))[1]: -1
}

const Engine = (canvas) => {
	const cellSize = 8;
	const shadowSize = 15;
	const w = Math.floor(canvas.width / cellSize)
	const h = Math.floor(canvas.height / cellSize)
	const _ctx = canvas.getContext('2d')
	const configs = []
	let _alive;
	let _rules;
	let count = 0
	let _currentLaw = 'init';
	let statistics = {}
	let loot = []
	let lootImg = ''
	let _power = 0;

	const incPower = () => _power += 100
	const decPower = () => _power > 0 ? _power-- : 0

	const setLoot = ({img, size}) => {
		lootImg = img
	}

	const addConfig = (config) => {
		configs[config.name] = { ...config, id: count++, count:0 }
	}
	const setConfig = (name) => {
		const { alive, rules} = configs[name]
		_alive = alive;
		_rules = rules
		_currentLaw = name
	}

	addConfig(initConfig)
	setConfig(_currentLaw)

	const lootSpawn = (time = 1000) => {
		const spawn = () => {
			const x = Math.random() * w * cellSize
			const y = Math.random() * h * cellSize
				loot.push({x:x, y:y})
		}
		setTimeout(spawn, Math.random() * time)
	}

	const drawLoot = () => {
		loot.forEach(({x,y}) => {  
				_ctx.drawImage(lootImg, x - 150, y - 80)
		})
	}

	const intersection = (x, y) => {
		const epsil = 50;
		loot = loot.filter(l => {
			const cond = (Math.abs(l.x - x) + Math.abs(l.y - y)) > epsil
			if (!cond) incPower()
			return cond
		})
	}
	const drawing = () => {
		if (_power > 0){
			const {x ,y} = cords;
			world[x + y * w].setState(1)
			world[x + y * w].setLaw(_currentLaw)
			draw(x, y)
			decPower()
		}
	}
		const init = () => {
		// удалить интервал
		canvas.addEventListener('mousemove',(event) =>{
			const {x, y} = getCords(event)
			cords = {x: Math.floor(x / cellSize), y: Math.floor(y / cellSize)}
			intersection(x, y)
		});
		canvas.addEventListener('mousedown',(event) =>{
			drawing()
			canvas.addEventListener('mousemove', drawing);
		});
		canvas.addEventListener('mouseup', () => {
			canvas.removeEventListener('mousemove',drawing);
		});
	}
	init()

	const Cell = (state, law = 'init') => (index) => {
		const j = Math.floor(index / w)
		const i = index % w
		let _law = law;
		const c = (i, j) => {
			const _i = i < 0 ? w + i: i < w ? i : w - i
			const _j = j < 0 ? h + j: j < h ? j : h - j
			return _i + _j * w
		}
		let _state = state;
		const setState = (s) => _state = s
		const getState = () => _state
		const setLaw = (l) => _law = l 
		const getLaw = () => _law
		const siblings = () => [
			world[c(i - 1, j - 1)],
			world[c(i - 1, j)],
			world[c(i - 1, j + 1)],
			world[c(i, j - 1)],
			world[c(i, j + 1)],
			world[c(i + 1, j - 1)],
			world[c(i + 1, j)],
			world[c(i + 1, j + 1)]
		] 
		return {
			getState,
			siblings,
			index,
			getLaw,
			setState,
			setLaw
		}
	}
	let world = [...Array(w * h).keys()].map(Cell(0))
	let cords = {}

	const getCords = event => {
		return {
			x:event.pageX - canvas.offsetLeft,
			y:event.pageY - canvas.offsetTop
		}
	}
	const Law = sibl => {
		const m = {}
		sibl.forEach(sib => _alive(sib.getState()) ?  m[sib.getLaw()]++: 0)
		let law = 'init'
		const {init, ...res} = m
		const keys = Object.keys(res)
		const values = Object.values(res)
		const id = argmax(values)
		return id < 0 ? 'init': keys[id]
	}

	const next = (cell) => {
		const prev = cell.getState()
		const siblings = cell.siblings()
		const law = Law(siblings)
		setConfig(law)
		if (_alive(cell.getState())) configs[law][count]++
		const res = siblings.reduce((a, x) => a + x.getState(), 0)
		return Cell(_rules(prev, res, _alive), law)(cell.index)
	}

	const epoch = () => {
		for (let el in configs) {
			configs[el][count] = 0
		}
		world = world.map(next)
	}
	const use = name => {
		setConfig(name)
	}

	const draw = (i, j) => {
		const cell = world[i + j * w]
		const id = cell.getLaw()
		const image = configs[id].image
		if (image) _ctx.drawImage(image, i * cellSize, j * cellSize)	
	}
	const draw1 = (i, j) => {
		const cell = world[i + j * w]
		const id = cell.getLaw()
		const shadow = configs[id].shadow
		if (shadow){
			_ctx.beginPath()
			_ctx.fillStyle = `rgba(${shadow.r},${shadow.g},${shadow.b},0.13)`
			_ctx.arc((i + 0.8)*cellSize, (j + 0.8)*cellSize, shadowSize, 0, 2*Math.PI)
			_ctx.fill()
		}	
	}

	const render = () =>  {
		epoch()
		_ctx.clearRect(0, 0, canvas.width, canvas.height)
		for (let i = 0; i < w; ++i){
			for (let j = 0; j < h; ++j){
				if (_alive(world[i + j*w].getState())) {
					draw(i,j)
				}
				else{
					draw1(i,j)
				}
			}
		}
		drawLoot()
	}
	const getStatistics = (name) => configs[name][count]
	const getPower = () => _power
	const initPower = (val) => _power = val 
	return {
		render,
		addConfig,
		use,
		getStatistics,
		setLoot,
		getPower,
		initPower
	}
}




export default Engine
