const initConfig = {
	alive: x => false,
	rules: x => x,
	name: 'init'
}


const Engine = (canvas) => {
	const cellSize = 5
	const w = Math.floor(canvas.width / cellSize)
	const h = Math.floor(canvas.height / cellSize)
	const _ctx = canvas.getContext('2d')
	const configs = []
	let _alive;
	let _rules;
	let count = 0
	let _currentLaw = 'init';

	const addConfig = ({ name, ...config }) => {
		configs[name] = { ...config, id: count++ }
	}
	const setConfig = (name) => {
		const { alive, rules } = configs[name]
		_alive = alive;
		_rules = rules
		_currentLaw = name
	}

	addConfig(initConfig)
	setConfig(_currentLaw)

	const drawing = () => {
		const {x ,y} = cords;
		world[x + y * w].setState(1)
		world[x + y * w].setLaw(_currentLaw)
		draw(x, y)
	}
		const init = () => {
		canvas.addEventListener('mousemove',(event) =>{
			const {x, y} = getCords(event)
			cords = {x: Math.floor(x / cellSize), y: Math.floor(y / cellSize)}
		});
		canvas.addEventListener('mousedown',(event) =>{
			drawing()
			canvas.addEventListener('mousemove', drawing);
		});
		canvas.addEventListener('mouseup', () => {
			canvas.removeEventListener('mousemove',drawing);
		});
	}
	init()

	const Cell = (state, law = 'init') => (index) => {
		const j = Math.floor(index / w)
		const i = index % w
		let _law = law;
		const c = (i, j) => {
			const _i = i < 0 ? 0: i < w ? i : w - i
			const _j = j < 0 ? 0: j < h ? j : h - j
			return _i + _j * w
		}
		let _state = state;
		const setState = (s) => _state = s
		const getState = () => _state
		const setLaw = (l) => _law = l 
		const getLaw = () => _law
		const siblings = () => [
			world[c(i - 1, j - 1)],
			world[c(i - 1, j)],
			world[c(i - 1, j + 1)],
			world[c(i, j - 1)],
			world[c(i, j + 1)],
			world[c(i + 1, j - 1)],
			world[c(i + 1, j)],
			world[c(i + 1, j + 1)]
		] 
		return {
			getState,
			siblings,
			index,
			getLaw,
			setState,
			setLaw
		}
	}
	let world = [...Array(w * h).keys()].map(Cell(0))
	let cords = {}

	const getCords = event => {
		return {
			x:event.pageX - canvas.offsetLeft,
			y:event.pageY - canvas.offsetTop
		}
	}
	const getLaw = sibl => {
		const m = [0,0,0,0]
		sibl.forEach(sib => m[world[sib.getLaw()].id]++)
		const [, res] = m
		return Math.max(...res)
	}

	const next = (cell) => {
		const prev = cell.getState()
		const siblings = cell.siblings()
		const law = getLaw(siblings) 
		setConfig(law)
		const res = siblings.reduce((a, x) => a + x.getState(), 0)
		return Cell(_rules(prev, res, _alive), law)(cell.index)
	}

	const epoch = () => {
		world = world.map(next)
	}
	const use = name => {
		setConfig(name)
	}

	const draw = (i, j) => {
		const state = world[i + j * w].getState()
		const id = configs[_currentLaw].id
		_ctx.beginPath()
		_ctx.fillStyle = `rgb(${ state * 60 + id * 40 },${ state * 70 + id*40 },${ state * 200 + id * 40 })`
		_ctx.arc(i*cellSize,j*cellSize,cellSize / 2, 0, Math.PI * 2)
		_ctx.fill()	
	}

	const render = () =>  {
		epoch()
		_ctx.clearRect(0, 0, canvas.width, canvas.height)
		for (let i = 0; i < w; ++i){
			for (let j = 0; j < h; ++j){
				if (_alive(world[i + j*w].getState())) {
					draw(i,j)
				}
			}
		}
	}

	return {
		render,
		addConfig,
		use,
	}
}




export default Engine
