import React, { createRef, Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Button, Container } from 'reactstrap'
import io from 'socket.io-client'


import './Game.css'
import {
  Chat,
  Statistics,
  GameBar
} from './modules'
import { 
  BlurContainer,
} from './components'

import {
  InitModal
} from './containers'

import { 
  initSocket,
  sendLoginInfo
} from './actions'

import Engine from './engine'

//rules for bonus 
  const alive1 = x => x >= 1

  const rules1 = (prev, res, alive) => {
    if (alive(prev)){
      if (res ==  2 || res == 3 ){
        return prev + 2
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive3 = x => x == 1

  const rules3 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 2 || res == 3 ){
        return 1
      }
      else {
        return 0
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive2 = x => x >= 1

  const rules2 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 1 || res == 4 ){
        return prev + 1
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        if (res == 4){
        return 2
      }
      else {
        return 0
      }
    }
  }



const iconUrls = {'blue':'./icon1.png', 'red':'./icon2.png','green': './icon3.png'}
const shadow = {'blue':{r:100,g:100,b:255},'red':{r:255,g:100,b:100},'green':{r:5,g:100,b:5}}
const urls = ['./hero1.png', './hero.png', './hero2.png', './waterbear.png']
const images = urls.map( url => new Image())
images.forEach((img, id) => img.src = urls[id])

const rules = [{alive:alive3, rules:rules3,image: images[0],shadow:shadow[0]}, {alive:alive3, rules:rules3, image:images[1],shadow:shadow[1]}, {alive:alive3, rules:rules3, image:images[2],shadow:shadow[2]}]

const configs = {
  urls: iconUrls,
  descriptions: ['супер быстрые','супер сильные',"супер плодовитые"] 
}


class Game extends Component {
state = {
  statistics: [],
  isLogin: false,
}
constructor(props){
   super(props)
   this._canvas = createRef()
   this.engine = null
}
componentDidMount(){
  this.init()
  this.socket = io()
  this.props.initSocket(this.socket)
  window.addEventListener('resize', this.init)
}
init = () => {
  this.resize()
  this.engine = Engine(this._canvas.current)
  rules.forEach(this.engine.addConfig)
  this.engine.setLoot({
    img:images['water']
  })
}

resize = () => {
  const parent = this._canvas.current.parentElement;
  this._canvas.current.width = parent.clientWidth
  this._canvas.current.height = parent.clientHeight
}

login = () => {
  this.change(this.props.race)()
  this.setState({
    isLogin: true
  })
  this.props.sendLoginInfo(this.socket, this.props.config)
}

logout = () => {
  this.setState({
    isLogin: false
  })
}

toggle = () => {
  let isOn = false;
  let id = null
  return () => {
    isOn = !isOn
    if (isOn) {
      id = setInterval(()=>{
        this.engine.render()
        this.setState({
          statistics:this.engine.getStatistics()
        })
      }, 100)
    }
    else{
      clearInterval(id)
    }
  }
}

change = (num) => () => {
  this.engine.use(this.props.race)
}

render(){
  const { statistics } = this.state
  const  color = shadow[this.props.race]
  return (
    <Fragment>
      <InitModal appear={!this.state.isLogin} login={this.login} configs={configs} />
       <BlurContainer  kernel={14} is={!this.state.isLogin}>
        <div  className='h-100 w-100 background tools'>
          <canvas  ref={this._canvas}/>
          <Statistics color={`rgb(${color.r},${color.g},${color.b}`}  value={statistics[this.props.race]} list={statistics} urls = {iconUrls}/>
          <Button  color='danger' onClick={this.toggle()}> on </Button>
          <Container className='d-flex'>
            <GameBar url={iconUrls[this.props.race]} />
          </Container>
        </div>
      </BlurContainer>
    </Fragment>
  )
}
}

const mapStateToProps = state => {
  return {
    race: state.race,
    config: {
      race: state.race,
      name: state.name
    }
  }
}

const mapDispatchToProps = dispatch => {
  return {
    initSocket: (socket) => dispatch(initSocket(socket)),
    sendLoginInfo: (socket, config) => dispatch(sendLoginInfo(socket, config)),
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Game)
