import {
	SELECT_NAME,
	SELECT_RACE,
	SET_POPULATION_SIZE,
	POST_MESSAGE
} from '../constants'

export const selectRace = (name) => {
	return {
		type:SELECT_RACE,
		payload: name
	}
}


export const postMessage = (message) => {
	return {
		type:POST_MESSAGE,
		payload: message
	}
}


export const selectName = (name) => {
	return {
		type:SELECT_NAME,
		payload: name
	}
}

export const setPopulationSize = (size) => {
	return {
		type:SELECT_RACE,
		payload: size
	}
}
