export const SELECT_NAME = 'SELECT_NAME'
export const SELECT_RACE = 'SELECT_RACE'
export const SET_POPULATION_SIZE = 'SET_POPULATION_SIZE'
export const POST_MESSAGE = 'POST_MESSAGE'

export const selectRace = (name) => {
	return {
		type:SELECT_RACE,
		payload: name
	}
}


export const postMessage = (message) => {
	return {
		type:POST_MESSAGE,
		payload: message
	}
}


export const selectName = (name) => {
	return {
		type:SELECT_NAME,
		payload: name
	}
}

export const setPopulationSize = (size) => {
	return {
		type:SELECT_RACE,
		payload: size
	}
}
