import React, { createRef, Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Button, Container } from 'reactstrap'
import {
  plus,
  minus
} from './actions'
import './Game.css'
import { 
  Statistics,
  Chat,
  Modal,
  BlurContainer
} from './components'
import Engine from './engine'

//rules for bonus 
  const alive1 = x => x >= 1

  const rules1 = (prev, res, alive) => {
    if (alive(prev)){
      if (res ==  2 || res == 3 ){
        return prev + 2
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive3 = x => x == 1

  const rules3 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 2 || res == 3 ){
        return 1
      }
      else {
        return 0
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive2 = x => x >= 1

  const rules2 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 1 || res == 4 ){
        return prev + 1
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        if (res == 4){
        return 2
      }
      else {
        return 0
      }
    }
  }


const shadow = [{r:255,g:100,b:100},{r:100,g:100,b:255},{r:5,g:100,b:5}]
const urls = ['./hero.png', './hero1.png', './hero2.png']
const images = urls.map( url => new Image())
images.forEach((img, id) => img.src = urls[id])

const rules = [{alive:alive3, rules:rules3,image: images[0],shadow:shadow[0]}, {alive:alive3, rules:rules3, image:images[1],shadow:shadow[1]}, {alive:alive3, rules:rules3, image:images[2],shadow:shadow[2]}]

class Game extends Component {
state = {
  statistics: [],
  isLogin: false
}
constructor(props){
   super(props)
   this._canvas = createRef()
   this.engine = null
}
componentDidMount(){
  this.init()
  window.addEventListener('resize', this.init)
}
init = () => {
  this.resize()
  this.engine = Engine(this._canvas.current)
  rules.forEach(this.engine.addConfig)
}

resize = () => {
  const parent = this._canvas.current.parentElement;
  this._canvas.current.width = parent.clientWidth
  this._canvas.current.height = parent.clientHeight
}

login = () => {
  this.setState({
    isLogin: true
  })
}

logout = () => {
  this.setState({
    isLogin: false
  })
}

toggle = () => {
  let isOn = false;
  let id = null
  return () => {
    isOn = !isOn
    if (isOn) {
      id = setInterval(()=>{
        this.engine.render()
        this.setState({
          statistics:this.engine.getStatistics()
        })
      }, 100)
    }
    else{
      clearInterval(id)
    }
  }
}

change = (num) => () => {
  this.engine.use(num)
}


render(){
  const { statistics } = this.state
  return (
    <Fragment>
      <Modal appear={!this.state.isLogin} close={this.login}>
        <p> hello </p>
      </Modal>
      <BlurContainer is={!this.state.isLogin}>
        <div  className='h-100 w-100 background tools'>
          <canvas  ref={this._canvas}/>
          <Statistics list={statistics} urls = {urls}/>
          <Button  color='danger' onClick={this.toggle()}> on </Button>
          <Button  color='success' onClick={this.change(1)}> hero1 </Button>
          <Button  color='success' onClick={this.change(2)}> hero2 </Button>
          <Button  color='success' onClick={this.change(3)}> hero3 </Button>    
          <Chat messages={[{url:urls[1],name:'nik', text:'kk'},{url:urls[1],name:'nik', text:'kk'},{url:urls[1],name:'nik', text:'kk'},{url:urls[2], name:'nik',text:'kekw'},{url:urls[0],name:'nik', text:'kek'}]}/>
        </div>
      </BlurContainer>
    </Fragment>
  )
}
}

/*
  background: rgba(0,0,0,0.7);
  filter: blur(4px);


              <Button  color='danger' onClick={this.toggle()}> on </Button>
            <Button  color='success' onClick={this.change(1)}> hero1 </Button>
            <Button  color='success' onClick={this.change(2)}> hero2 </Button>
            <Button  color='success' onClick={this.change(3)}> hero3 </Button>
*/


export default Game
