import React, { createRef, Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Button, Container } from 'reactstrap'
import {
  plus,
  minus
} from './actions'
import './Game.css'
import Engine from './engine'


class Game extends Component {
constructor(props){
   super(props)
   this._canvas = createRef()
   this.engine = null
}
componentDidMount(){
  init()
  window.addEventListener('resize', init)
}
init = () => {
  this.resize()
  this.engine = Engine(this._canvas.current)
}

resize = () => {
  const parent = this._canvas.current.parentElement;
  this._canvas.current.width = parent.clientWidth
  this._canvas.current.height = parent.clientHeight
}

toggle = () => {
  let isOn = false;
  let id = null
  return () => {
    isOn = !isOn
    if (isOn) {
      id = this.engine.render()
    }
    else{
      clearInterval(id)
    }
  }
}

render(){
  return (
      <div  className='h-100 w-100 background'>
        <canvas  ref={this._canvas}>
        </canvas>
        <Button  color='danger' onClick={this.toggle()}> on </Button>
      </div>
  )
}
}

export default Game
