import React, {Fragment} from 'react'
import './Statistics.css'
import { Container } from 'reactstrap'
import { GameBar } from '../'
import { Message } from '../../components'
import { connect } from 'react-redux'

const StatisticsItem = ({score, url, messages}) => {
	return (
			<Container className='statistic-item'>
				<img width='50px' height='50px' src={url}/>
				<p> {score} </p>
				<Message placement='bottom' text={messages} />
			</Container>
	)
}


const StatisticsUnconnect = ({value, color, list, messages}) => {
	return (
		<Fragment>
			<ul className='statistics statistics-list'>
				{list.map((el, id) => <li key={id}> <StatisticsItem  messages={messages[el.race]} score={el.score} url={el.url}/> </li>)}			
			</ul>
			<div className='game-bar' style={{height:value, background:color}}/> 
		</Fragment>
		)
}


const mapStateToProps = state => {
	return {
		messages: state.messages
	}
}

const Statistics = connect(mapStateToProps)(StatisticsUnconnect)

export {
	Statistics
}
