import React, { createRef, Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Button, Container } from 'reactstrap'
import {
  plus,
  minus
} from './actions'
import './Game.css'
import Engine from './engine'

//rules for bonus 
  const alive1 = x => x >= 1

  const rules1 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 2 || res == 3 ){
        return prev + 2
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive3 = x => x == 1

  const rules3 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 2 || res == 3 ){
        return 1
      }
      else {
        return 0
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive2 = x => x >= 1

  const rules2 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 1 || res == 4 ){
        return prev + 1
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        if (res == 4){
        return 2
      }
      else {
        return 0
      }
    }
  }

const rules = [{alive:alive1, rules:rules1}, {alive:alive2, rules:rules2}, {alive:alive3, rules:rules3}]

class Game extends Component {
constructor(props){
   super(props)
   this._canvas = createRef()
   this.engine = null
}
componentDidMount(){
  this.init()
  window.addEventListener('resize', this.init)
}
init = () => {
  this.resize()
  this.engine = Engine(this._canvas.current)
  this.engine.render()
}

resize = () => {
  const parent = this._canvas.current.parentElement;
  this._canvas.current.width = parent.clientWidth
  this._canvas.current.height = parent.clientHeight
}

toggle = () => {
  let isOn = false;
  let id = null
  return () => {
    isOn = !isOn
    if (isOn) {
      id = setInterval(this.engine.render, 200)
    }
    else{
      clearInterval(id)
    }
  }
}

change = (num) => () => {
  this.engine.setConfig(rules[num])
}


render(){
  return (
      <div  className='h-100 w-100 background'>
        <canvas  ref={this._canvas}>
        </canvas>
        <Container>
          <Button  color='danger' onClick={this.toggle()}> on </Button>
          <Button  color='success' onClick={this.change(0)}> hero1 </Button>
          <Button  color='success' onClick={this.change(1)}> hero2 </Button>
          <Button  color='success' onClick={this.change(2)}> hero3 </Button>
        </Container>
      </div>
  )
}
}

export default Game
