import React  from 'react'
import './Modal.css'
import {
	Container
} from 'reactstrap'

export const Modal = ({closeKey ,...props}) => {
	const keyClose = event => {
		if (event.keyCode == closeKey){
			props.close()
		}
	}
	return (
		props.appear && 
		<div onKeyDown={keyClose} className='modal-layer'>
			<Container className='inner-modal-layer'>
				<p className='modal-cross' onClick={props.close}>
					 X 
				</p>
				{ props.children }
			</Container>
		</div> )
}
