import React, { Fragment } from 'react'
import './GameBar.css'
import { Input, Container, Button } from 'reactstrap'
import { connect } from 'react-redux'
import {
	Message, ChatInput
} from '../../components'



const GameBar = ({value, color, url, text}) => {
	return (
		<Container fluid className='game-bar_container'>
				<img width='80px' height='80px' src={url}/>
 				<Message placement='top' text={text}/>
			<ChatInput/>
			<div className='game-bar' style={{height:value, background:color}}/> 
		</Container>
	)
}

const mapStateToProps = state => {
	return {
		text: state.message
	}
}

const ConnectedComponent = connect(mapStateToProps)(GameBar)

export {
	ConnectedComponent as GameBar
}
