import React, { Fragment, Component } from 'react'
import { Container, Button } from 'reactstrap'
import './Modal.css'


const ModalView = props => (rops.appear && <Container className='modal-layer'>
					<div className='inner-modal-layer'>
						<p className='cross' onClick={props.off}> X </p>
						{ props.children }
					</div>
	</Container>)

export class Modal extends Component{
	state = {
		appear: false
	}
	toggle = () => {
		this.setState({
			appear: !this.state.appear
		})
	}
	off = () => {
		this.setState({
			appear:false
		})
	}
	render(){
		const { children } = this.props
		return(
			<Fragment>
				<Container fluid className='background d-flex flex-column w-100 h-100'>
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				<ModalView off={this.off} appear={this.state.appear} />}
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				</Container>
			</Fragment>
		)
	}
}
