import React from 'react'
import './Statistics.css'
import { Container } from 'reactstrap'




const StatisticsItem = ({score, img}) => {
	return (
			<Container>
				{img}
				<h3> {score} </h3>
			</Container>
	)
}

export const Statistics = ({list, images}) => {
	return (
			<ul className='statistics statistics-list'>
				{list.map((el, id) => <li key={id}> <StatisticsItem score={el} image={images[id]}/> </li>)}			
			</ul>
		)
}
Statistics.defaultProps = {
	list: []
}
