import React, {Fragment, Component, PureComponent} from 'react'
import './Message.css'
import {
	PopoverBody,
	Popover
} from 'reactstrap'



const Message = ({text, placement, isOpen}) => {
	return(
		isOpen && <div>
			{text}
		</div>
	)
}


class MessageContainer extends PureComponent{
	state = {
		is: true
	}

	componentDidMount(){
		setTimeout(this.close, 2000)
	}
	componentDidUpdate(){
		this.setState({is: true})
		setTimeout(this.close, 2000)
	}

	close = () => this.setState({is: false})
	render(){
		const {text, isOpen} = this.props
		return(
				<Message text='kek' placement='bottom' isOpen={this.state.is}/>
		)
	}
}

export {
	MessageContainer as Message
}
