const initConfig = {
	alive: x => x >= 1,
	rules: x => x,
	name: 'init'
}

const argmax = array => array.map((x, i) => [x, i]).reduce((r, a) => (a[0] > r[0] ? a : r))[1];

const Engine = (canvas) => {
	const cellSize = 10;
	const w = Math.floor(canvas.width / cellSize)
	const h = Math.floor(canvas.height / cellSize)
	const _ctx = canvas.getContext('2d')
	const configs = []
	let _alive;
	let _rules;
	let count = 0
	let _currentLaw = 0;
	let statistics = []

	const addConfig = (config) => {
		configs.push({ ...config, id: count++ })
	}
	const setConfig = (id) => {
		const { alive, rules} = configs[id]
		_alive = alive;
		_rules = rules
		_currentLaw = id
	}

	addConfig(initConfig)
	setConfig(_currentLaw)

	const drawing = () => {
		const {x ,y} = cords;
		world[x + y * w].setState(1)
		world[x + y * w].setLaw(_currentLaw)
		draw(x, y)
	}
		const init = () => {
		canvas.addEventListener('mousemove',(event) =>{
			const {x, y} = getCords(event)
			cords = {x: Math.floor(x / cellSize), y: Math.floor(y / cellSize)}
		});
		canvas.addEventListener('mousedown',(event) =>{
			drawing()
			canvas.addEventListener('mousemove', drawing);
		});
		canvas.addEventListener('mouseup', () => {
			canvas.removeEventListener('mousemove',drawing);
		});
	}
	init()

	const Cell = (state, law = 0) => (index) => {
		const j = Math.floor(index / w)
		const i = index % w
		let _law = law;
		const c = (i, j) => {
			const _i = i < 0 ? w + i: i < w ? i : w - i
			const _j = j < 0 ? h + j: j < h ? j : h - j
			return _i + _j * w
		}
		let _state = state;
		const setState = (s) => _state = s
		const getState = () => _state
		const setLaw = (l) => _law = l 
		const getLaw = () => _law
		const siblings = () => [
			world[c(i - 1, j - 1)],
			world[c(i - 1, j)],
			world[c(i - 1, j + 1)],
			world[c(i, j - 1)],
			world[c(i, j + 1)],
			world[c(i + 1, j - 1)],
			world[c(i + 1, j)],
			world[c(i + 1, j + 1)]
		] 
		return {
			getState,
			siblings,
			index,
			getLaw,
			setState,
			setLaw
		}
	}
	let world = [...Array(w * h).keys()].map(Cell(0))
	let cords = {}

	const getCords = event => {
		return {
			x:event.pageX - canvas.offsetLeft,
			y:event.pageY - canvas.offsetTop
		}
	}
	const Law = sibl => {
		const m = Array(configs.length).fill(0)
		sibl.forEach(sib => m[sib.getLaw()]++)
		const [, ...res] = m
		return res.reduce((a,r) => a + r, 0) ? argmax(res) + 1: 0
	}

	const next = (cell) => {
		const prev = cell.getState()
		const siblings = cell.siblings()
		const law = Law(siblings)
		setConfig(law)
		if (_alive(cell.getState())) statistics[law]++
		const res = siblings.reduce((a, x) => a + x.getState(), 0)
		return Cell(_rules(prev, res, _alive), law)(cell.index)
	}

	const epoch = () => {
		statistics = Array(configs.length).fill(0)
		world = world.map(next)
	}
	const use = id => {
		setConfig(id)
	}

	const draw = (i, j) => {
		const cell = world[i + j * w]
		const state = cell.getState()
		const id = cell.getLaw()
		const image = configs[id].image
		_ctx.beginPath()
		_ctx.fillStyle = `rgb(${ state * 30 + id * 40  },${ state * 70 + id*40 },${ state * 180 + id * 40 })`
		_ctx.arc(i*cellSize,j*cellSize,cellSize / 2, 0, Math.PI * 2)
		_ctx.fill()

		//if (image) _ctx.drawImage(image, i * cellSize, j * cellSize)	
	}

	const render = () =>  {
		epoch()
		_ctx.clearRect(0, 0, canvas.width, canvas.height)
		for (let i = 0; i < w; ++i){
			for (let j = 0; j < h; ++j){
				if (_alive(world[i + j*w].getState())) {
					draw(i,j)
				}
			}
		}
	}
	const getStatistics = () => statistics.splice(1)
	return {
		render,
		addConfig,
		use,
		getStatistics
	}
}




export default Engine
