import React, {Fragment, Component, PureComponent} from 'react'
import './Message.css'
import {
	Container
} from 'reactstrap'


const stl = (position) =>  {
	const pos = (position === 'bottom') ? '0px,70px' : '50px,-50px'
	return {
		transform: `translate(${pos})`
	}
}

class Message extends Component {
	componentDidMount(){
		this.id = setTimeout(this.props.close, 2000)
	}

	componentWillUnmount(){
		clearTimeout(this.id)
	}
	render(){
		const {placement, text} = this.props
		return(
			<div className='my_message'  style={stl(placement)}>
				{text}
			</div>
		)
	}
}


class MessageContainer extends Component{
	state = {
		is:true
	}

	close = () => this.setState({is: false})

	render(){
		const { text } = this.props
		return(
			<Fragment>
				{this.state.is && <Message  close={this.close} text={text} placement={this.props.placement}/>}
			</Fragment>
		)
	}
}

export {
	MessageContainer as Message
}
