import React, {Fragment, Component, PureComponent} from 'react'
import './Message.css'
import {
	PopoverBody,
	Popover
} from 'reactstrap'



const Message = ({text, placement}) => {
	return(
		<div>
			{text}
		</div>
	)
}


class MessageContainer extends PureComponent{
	state = {
		text: '',
		is: true
	}

	static getDerivedStateFromProps(props, state){
		if (state.text !== props.text){
			console.log(1)
			return {
				text: props.text,
				is: false
			}
		}
		else{
			return null
		}
	}

	componentDidUpdate(){
		console.log(3)
		setTimeout(this.close, 2000)
	}
	close = () => this.setState({is: false})

	render(){
		const {text, isOpen} = this.props
		return(
				this.state.is && <Message text={text} placement='bottom'/>
		)
	}
}

export {
	MessageContainer as Message
}
