import React, { Fragment, Component } from 'react'
import { Container, Button } from 'reactstrap'
import './Modal.css'


const ModalView = props => {
		return (
			 props.appear && <div className='modal-layer'>
					<div className='inner-modal-layer'>
						<p className='modal-cross' onClick={props.off}> X </p>
						{ props.children }
					</div>
	</div>
	)
}


const BlurLayer = props => 
		<div className='w-100 h-100' style={ props.is ? {filter:`blur(${props.kernel}px)`} : {}}>
			{props.children}
		</div>
BlurLayer.defaultProps = {
	kernel: 1
}

export class Modal extends Component{
	state = {
		appear: false
	}
	toggle = () => {
		this.setState({
			appear: !this.state.appear
		})
	}
	off = () => {
		this.setState({
			appear:false
		})
	}
	render(){
		const { children } = this.props
		return(
			<Fragment>
			<BlurLayer is={this.state.appear} kernel={10} >
				<Container fluid className='background d-flex flex-column w-100 h-100'>
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				<Button onClick={this.toggle}> click </Button>
				</Container>
			</BlurLayer>
			<ModalView off={this.off} appear={this.state.appear}>
				{children}
			</ModalView>
			</Fragment>
		)
	}
}
