import React, { createRef, Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Button, Container } from 'reactstrap'
import io from 'socket.io-client'
import { Route, withRouter } from 'react-router-dom'
import {Howl, Howler} from 'howler'
import './Game.css'
import {
  Chat,
  Statistics,
  GameBar
} from './modules'
import { 
  BlurContainer,
} from './components'

import {
  InitModal
} from './containers'

import { 
  initSocket,
  sendLoginInfo,
  login
} from './actions'

import Engine from './engine'


const playSounds = () => {
  const sounds = [
    'music/Polyphia G.O.A.T. (Official Music Video).mp3',
    'music/Polyphia – O.D..mp3',
    'music/Polyphia - Crush.mp3'
  ]
  const sound = new Howl({
    src:sounds,
    autoplay: true,
    volume: 0.9,
  });
}


//rules for bonus 
  const alive1 = x => x >= 1

  const rules1 = (prev, res, alive) => {
    if (alive(prev)){
      if (res ==  2 || res == 3 ){
        return prev + 2
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive3 = x => x == 1

  const rules3 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 2 || res == 3 ){
        return 1
      }
      else {
        return 0
      }
    }
    else{
        return (res == 3)
    }
  }

  const alive2 = x => x >= 1

  const rules2 = (prev, res, alive) => {
    if (alive(prev)){
      if (res == 1 || res == 4 ){
        return prev + 1
      }
      else {
        return (res - prev < 0)
      }
    }
    else{
        if (res == 4){
        return 2
      }
      else {
        return 0
      }
    }
  }



const iconUrls = ['./icon1.png','./icon2.png','./icon3.png']
const iconObj = {blue:'./icon1.png',red:'./icon2.png',green:'./icon3.png'}
const shadow = {'blue':{r:100,g:100,b:255},'red':{r:255,g:100,b:100},'green':{r:5,g:100,b:5}}
const urls = ['./hero1.png', './hero.png', './hero2.png', './waterbear.png']
const images = urls.map( url => new Image())
images.forEach((img, id) => img.src = urls[id])

const rules = [{name:'blue',alive:alive3, rules:rules3,image: images[0],shadow:shadow[0]}, {name:'red',alive:alive3, rules:rules3, image:images[1],shadow:shadow[1]}, {name:'green',alive:alive3, rules:rules3, image:images[2],shadow:shadow[2]}]

const configs = {
  urls: iconUrls,
  descriptions: {blue:'супер быстрые',red:'супер сильные',green:"супер плодовитые"}
}


class Game extends Component {
state = {
  statistics: [],
}
constructor(props){
   super(props)
   this._canvas = createRef()
   this.engine = null
}
componentDidMount(){
  this.init()
  this.socket = io()
  this.props.initSocket(this.socket)
  window.addEventListener('resize', this.init)
}
init = () => {
  this.resize()
  this.engine = Engine(this._canvas.current)
  rules.forEach(this.engine.addConfig)
  this.engine.setLoot({
    img:images[3]
  })
}

resize = () => {
  const parent = this._canvas.current.parentElement;
  this._canvas.current.width = parent.clientWidth
  this._canvas.current.height = parent.clientHeight
}

login = () => {
  this.change(this.props.race)()
  console.log('123124124124')
  this.props.Login()
  this.props.sendLoginInfo(this.socket, this.props.config)
}

toggle = () => {
  let isOn = false;
  let id = null
  return () => {
    isOn = !isOn
    if (isOn) {
      id = setInterval(()=>{
        this.engine.render()
        this.setState({
          statistics:this.engine.getStatistics()
        })
      }, 100)
    }
    else{
      clearInterval(id)
    }
  }
}

change = (num) => () => {
  this.engine.use(this.props.race)
}

  render(){
    const { statistics } = this.state
    const  color = shadow[this.props.race]
    const stats = [
      {race:'blue', url: iconUrls[0], score: 26},
      {race:'red', url: iconUrls[1], score: 6},
      {race:'green', url: iconUrls[2], score: 120}
    ]
    const MainComponent = () =>
    <BlurContainer  kernel={14} is={!this.props.isLogin}>
        <div  className='h-100 w-100 background tools'>
          <canvas  ref={this._canvas}/>
          <Statistics list={stats}/>
          <Container className='d-flex'>
            <GameBar color={`rgb(${color.r},${color.g},${color.b}`}  value={100}  url={iconObj[this.props.race]} />
          </Container>
        </div>
      </BlurContainer>

    return (
      <Fragment>
        <Route exact path='/' render={ () => {
            return <InitModal  login={this.login} configs={configs} />
        }}/>
        <MainComponent/>
      </Fragment>
    )
  }
}

//Вернуть и очень много работы с engine


//  <Statistics color={`rgb(${color.r},${color.g},${color.b}`}  value={statistics[this.props.race]} list={statistics} urls = {iconUrls}/>
// <Button  color='danger' onClick={this.toggle()}> on </Button>

const mapStateToProps = state => {
  return {
    isLogin: state.isLogin,
    race: state.race,
    config: {
      race: state.race,
      name: state.name
    }
  }
}

const mapDispatchToProps = dispatch => {
  return {
    initSocket: (socket) => dispatch(initSocket(socket)),
    Login: () => dispatch(login()),
    sendLoginInfo: (socket, config) => dispatch(sendLoginInfo(socket, config)),
  }
}
export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Game))
