import React, {Fragment, Component, PureComponent} from 'react'
import './Message.css'
import {
	Container
} from 'reactstrap'


const stl = (position) =>  {
	const pos = (position === 'bottom') ? '0px,70px' : '-50px,-50px'
	return {
		transform: `translate(${pos})`
	}
}

const Message = ({text, placement}) => {
	return(
		<div className='my_message'  style={stl(placement)}>
			<p className='discription-text'>{text}</p>
		</div>
	)
}


class MessageContainer extends Component{
	state = {
		is:false
	}
	componentWillReceiveProps(nextProps){
		if (nextProps.children !== this.props.children){
			this.setState({is: true})
		}
	}
	componentDidUpdate(props, state){
			if (this.state.is){
				clearTimeout(this.id) 
				this.id = setTimeout(this.close, 2000)
			}
	}

	close = () => this.setState({is: false})

	render(){
		return(
			<Fragment>
				{this.state.is && this.props.children}
			</Fragment>
		)
	}
}

class MessageN extends Component { 
	render(){
		const {text, placement} = this.props
		return(
			<MessageContainer>
				<Message text={text} placement={placement}/>
			</MessageContainer>
		)
	}
}


export {
	MessageN as Message
}
