import React, { createRef, Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Button } from 'reactstrap'
import {
  plus,
  minus
} from './actions'
import './Game.css'
import Engine from './engine'


class Game extends Component {
constructor(props){
   super(props)
   this._canvas = createRef()
}
componentDidMount(){
  console.log(1)
  this.resize()
  const engine = Engine(this._canvas.current)
  engine.render()
}

resize = () => {
  const parent = this._canvas.current.parentElement;
  this._canvas.current.width = parent.clientWidth
  this._canvas.current.height = parent.clientHeight
}

render(){
  return (
      <div  className='h-100 w-100 background'>
        <canvas  ref={this._canvas}>
        </canvas>
      </div>
  )
}
}

export default Game
